'use strict';

var generateCategoricalChart = require('./generateCategoricalChart.cjs');
var Bar = require('../cartesian/Bar.cjs');
var XAxis = require('../cartesian/XAxis.cjs');
var YAxis = require('../cartesian/YAxis.cjs');
var CartesianUtils = require('../util/CartesianUtils.cjs');

/**
 * @fileOverview Bar Chart
 */
var BarChart = generateCategoricalChart.generateCategoricalChart({
  chartName: 'BarChart',
  GraphicalChild: Bar.Bar,
  defaultTooltipEventType: 'axis',
  validateTooltipEventTypes: ['axis', 'item'],
  axisComponents: [{
    axisType: 'xAxis',
    AxisComp: XAxis.XAxis
  }, {
    axisType: 'yAxis',
    AxisComp: YAxis.YAxis
  }],
  formatAxisMap: CartesianUtils.formatAxisMap
});

exports.BarChart = BarChart;
