'use strict';

var generateCategoricalChart = require('./generateCategoricalChart.cjs');
var Line = require('../cartesian/Line.cjs');
var XAxis = require('../cartesian/XAxis.cjs');
var YAxis = require('../cartesian/YAxis.cjs');
var CartesianUtils = require('../util/CartesianUtils.cjs');

/**
 * @fileOverview Line Chart
 */
var LineChart = generateCategoricalChart.generateCategoricalChart({
  chartName: 'LineChart',
  GraphicalChild: Line.Line,
  axisComponents: [{
    axisType: 'xAxis',
    AxisComp: XAxis.XAxis
  }, {
    axisType: 'yAxis',
    AxisComp: YAxis.YAxis
  }],
  formatAxisMap: CartesianUtils.formatAxisMap
});

exports.LineChart = LineChart;
