'use strict';

var generateCategoricalChart = require('./generateCategoricalChart.cjs');
var PolarAngleAxis = require('../polar/PolarAngleAxis.cjs');
var PolarRadiusAxis = require('../polar/PolarRadiusAxis.cjs');
var PolarUtils = require('../util/PolarUtils.cjs');
var Pie = require('../polar/Pie.cjs');

/**
 * @fileOverview Pie Chart
 */
var PieChart = generateCategoricalChart.generateCategoricalChart({
  chartName: 'PieChart',
  GraphicalChild: Pie.Pie,
  validateTooltipEventTypes: ['item'],
  defaultTooltipEventType: 'item',
  legendContent: 'children',
  axisComponents: [{
    axisType: 'angleAxis',
    AxisComp: PolarAngleAxis.PolarAngleAxis
  }, {
    axisType: 'radiusAxis',
    AxisComp: PolarRadiusAxis.PolarRadiusAxis
  }],
  formatAxisMap: PolarUtils.formatAxisMap,
  defaultProps: {
    layout: 'centric',
    startAngle: 0,
    endAngle: 360,
    cx: '50%',
    cy: '50%',
    innerRadius: 0,
    outerRadius: '80%'
  }
});

exports.PieChart = PieChart;
